/*********************************************************************
*
*       Dialog callback routine
*/
static void _cbDialog(WM_MESSAGE * pMsg) 
{
	int NCode, Id;
	WM_HWIN hWin = pMsg->hWin;
	//WM_HWIN hDlg;
	WM_HWIN	hEdit0_Item = WM_GetDialogItem(hWin, GUI_ID_EDIT0);
	WM_HWIN	hEdit1_Item = WM_GetDialogItem(hWin, GUI_ID_EDIT1);
	WM_HWIN	hEdit2_Item = WM_GetDialogItem(hWin, GUI_ID_EDIT2);
	WM_HWIN	hEdit3_Item = WM_GetDialogItem(hWin, GUI_ID_EDIT3);
	WM_HWIN	hEdit4_Item = WM_GetDialogItem(hWin, GUI_ID_EDIT4);
	WM_HWIN	hEdit5_Item = WM_GetDialogItem(hWin, GUI_ID_EDIT5);
	
	switch (pMsg->MsgId) {
	case WM_PAINT:
		//PaintDialog(pMsg);
		EDIT_SetDecMode(hEdit0_Item, REF_CLK, 1, 100, 0, 0);
		EDIT_SetDecMode(hEdit1_Item, Register_Buf[6], 100, 4400000, 0, 0);
		EDIT_SetDecMode(hEdit2_Item, Register_Buf[7], 35, 4400, 0, 0);
		EDIT_SetDecMode(hEdit3_Item, Register_Buf[8], 35, 4400, 0, 0);
		EDIT_SetDecMode(hEdit4_Item, Register_Buf[9], 100, 50000, 0, 0);
		EDIT_SetDecMode(hEdit5_Item, Register_Buf[10], 10, 100000, 0, 0);
		break;
	case WM_INIT_DIALOG:
		//InitDialog(pMsg);
		break;
	case WM_KEY:
		switch (((WM_KEY_INFO*)(pMsg->Data.p))->Key) {
		case GUI_KEY_ESCAPE:
			GUI_EndDialog(hWin, 1);
			break;
		case GUI_KEY_ENTER:
			GUI_EndDialog(hWin, 0);
			break;
		}
		break;
	case WM_NOTIFY_PARENT:
		Id = WM_GetId(pMsg->hWinSrc); 
		NCode = pMsg->Data.v;  
		if (Key_Return_Flag) {
			Key_Return_Flag = 0;
			EDIT_SetValue(hEdit1_Item, Register_Buf[6]);
			EDIT_SetValue(hEdit2_Item, Register_Buf[7]);
			EDIT_SetValue(hEdit3_Item, Register_Buf[8]);
			EDIT_SetValue(hEdit4_Item, Register_Buf[9]);
			EDIT_SetValue(hEdit5_Item, Register_Buf[10]);
			Key_SW_Flag = 1;
			Register_Index = 0;
		}  
		switch (Id) {
		case GUI_ID_OK:
			if (NCode == WM_NOTIFICATION_RELEASED)
				GUI_EndDialog(hWin, 0);
			break;
		case GUI_ID_CANCEL:
			if (NCode == WM_NOTIFICATION_RELEASED)
				GUI_EndDialog(hWin, 0);
			break;
		case GUI_ID_BUTTON0:
			switch (NCode) {
			case WM_NOTIFICATION_RELEASED:
				OnButtonClicked_RF_OUT(pMsg);
				break;
			}
			break;
		case GUI_ID_BUTTON1:
			switch (NCode) {
			case WM_NOTIFICATION_RELEASED:
				OnButtonClicked_Start_F(pMsg);
				if (Key_SW_Flag == 0) {
					Key_SW_Flag = 2;
					Register_Index = 2;//Freq0
					CreateSubdialog();
				} else
					Key_SW_Flag = 0;													
				break;
			}
			break;
		case GUI_ID_BUTTON2:
			switch (NCode) {
			case WM_NOTIFICATION_RELEASED:
				OnButtonClicked_Stop_F(pMsg);
				break;
			}
			break;
		case GUI_ID_BUTTON3:
			switch (NCode) {
			case WM_NOTIFICATION_RELEASED:
				OnButtonClicked_Delta_F(pMsg);
				break;
			}
			break;
		case GUI_ID_BUTTON4:
			switch (NCode) {
			case WM_NOTIFICATION_RELEASED:
				OnButtonClicked_Ramp_Clk(pMsg);
				break;
			}
			break;
		case GUI_ID_BUTTON5:
			switch (NCode) {
			case WM_NOTIFICATION_RELEASED:
				OnButtonClicked_Start_SW(pMsg);
				break;
			}
			break;
		case GUI_ID_BUTTON6:
			switch (NCode) {
			case WM_NOTIFICATION_RELEASED:
				OnButtonClicked_Stop_SW(pMsg);
				break;
			}
			break;
		case GUI_ID_EDIT1:
			switch (NCode) {
			case WM_NOTIFICATION_GOT_FOCUS:
			case WM_NOTIFICATION_RELEASED:
				OnEditClicked_RF_Out(pMsg);
				if (Key_SW_Flag == 0) {
					Key_SW_Flag = 1;
					Register_Index = 1;//Freq0
					CreateSubdialog();
					WM_SetFocus(0);
				} else
					Key_SW_Flag = 0;												
				break;
			}
			break;
		case GUI_ID_EDIT2:
			switch (NCode) {
			case WM_NOTIFICATION_GOT_FOCUS://case WM_NOTIFICATION_CLICKED:
				OnEditClicked_Start_F(pMsg);
				if (Key_SW_Flag == 0) {
					Key_SW_Flag = 2;
					Register_Index = 2;//Freq0
					CreateSubdialog();
				} else
					Key_SW_Flag = 0;													
				break;
			}
			break;
		case GUI_ID_EDIT3:
			switch (NCode) {
			case WM_NOTIFICATION_GOT_FOCUS://case WM_NOTIFICATION_CLICKED:
				OnEditClicked_Stop_F(pMsg);
				if (Key_SW_Flag == 0) {
					Key_SW_Flag = 3;
					Register_Index = 3;//Freq0
					CreateSubdialog();
				} else
					Key_SW_Flag = 0;													
				break;
			}
			break;
		case GUI_ID_EDIT4:
			switch (NCode) {
			case WM_NOTIFICATION_GOT_FOCUS:
			case WM_NOTIFICATION_RELEASED:
				OnEditClicked_Delta_F(pMsg);
				if (Key_SW_Flag == 0) {
					Key_SW_Flag = 4;
					Register_Index = 4;//Freq0
					CreateSubdialog();
				} else
					Key_SW_Flag = 0;													
				break;
			}
			break;
		case GUI_ID_EDIT5:
			switch (NCode) {
			case WM_NOTIFICATION_GOT_FOCUS:
			case WM_NOTIFICATION_RELEASED:
				OnEditClicked_Ramp_Clk(pMsg);
				if (Key_SW_Flag == 0) {
					Key_SW_Flag = 5;
					Register_Index = 5;   //Ramp_clk
					CreateSubdialog();
				} else
					Key_SW_Flag = 0;	
				break;
			}
			break;

		}
		break;
	default:
		WM_DefaultProc(pMsg);
	}
}

